import React, { useState } from 'react';

const Dashboard = ({ user, expenses, total, onAddExpense, onDeleteExpense, onLogout }) => {
  const [amount, setAmount] = useState('');
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState('Food');

  const categories = ['Food', 'Transport', 'Shopping', 'Entertainment', 'Bills', 'Other'];

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!amount || !description) {
      alert('Please fill all fields');
      return;
    }

    onAddExpense({
      amount: parseFloat(amount),
      description,
      category
    });

    // Reset form
    setAmount('');
    setDescription('');
    setCategory('Food');
  };

  return (
    <div className="dashboard">
      <header className="header">
        <h1>üí∞ Expense Tracker</h1>
        <div className="user-info">
          <span>Welcome, {user.name}!</span>
          <button onClick={onLogout} className="logout-btn">Logout</button>
        </div>
      </header>

      <div className="dashboard-content">
        <div className="total-section">
          <h2>Total Expenses: ${total.toFixed(2)}</h2>
        </div>

        <div className="add-expense-section">
          <h3>Add New Expense</h3>
          <form onSubmit={handleSubmit} className="expense-form">
            <input
              type="number"
              step="0.01"
              placeholder="Amount"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              required
            />
            <input
              type="text"
              placeholder="Description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
            />
            <select value={category} onChange={(e) => setCategory(e.target.value)}>
              {categories.map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
            <button type="submit">Add Expense</button>
          </form>
        </div>

        <div className="expenses-section">
          <h3>Recent Expenses ({expenses.length})</h3>
          <div className="expenses-list">
            {expenses.length === 0 ? (
              <p>No expenses yet. Add your first expense above!</p>
            ) : (
              expenses.map(expense => (
                <div key={expense._id} className="expense-item">
                  <div className="expense-info">
                    <span className="expense-amount">${expense.amount.toFixed(2)}</span>
                    <span className="expense-description">{expense.description}</span>
                    <span className="expense-category">{expense.category}</span>
                    <span className="expense-date">{new Date(expense.date).toLocaleDateString()}</span>
                  </div>
                  <button 
                    onClick={() => onDeleteExpense(expense._id, expense.amount)}
                    className="delete-btn"
                  >
                    ‚ùå
                  </button>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
